public static string GetPath(string sourceFilePath, string distanceFilePath)
  {
      string fileName = Path.GetFileName(sourceFilePath);
      string localDirPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), Path.GetDirectoryName(distanceFilePath));
      string localFilePath = Path.Combine(localDirPath, fileName);

      if (File.Exists(sourceFilePath))
      {              
          return sourceFilePath;
      }
      else
      {
          if (File.Exists(localFilePath)) return localFilePath;
      }

      return string.Empty;
  }
